import axios from 'axios';
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'



const URI = 'http://localhost:8000/books'

const CompShowBooks = () => {
    const [books, setBook] = useState([])
    useEffect( () => {
        getBooks()
    },[])

    const getBooks = async () => {
        const res = await axios.get(URI)
        setBook(res.data)
    }

    const deleteBook = async (id) => {
        await axios.delete(`${URI}/${id}`)
        getBooks()
    }

    return (
        <div className='container'>
            <div className='row'>
                <div className='colum'>
                    <Link to="/create" className='btn btn-primary mt-2 mb-2' ><i className="fa-solid fa-plus"></i></Link>
                    <table className="table table-hover">
                        <thead className='table-primary'>
                            <tr>
                                <th>Titulo</th>
                                <th>Autor</th>
                                <th>Fecha de publicaci√≥n</th>
                                <th>Genero</th>
                                <th>Aciones</th>
                            </tr>
                        </thead>
                        <tbody>
                            { books.map ( (book) => (
                                <tr key={ book.id }>
                                    <td> { book.title } </td>
                                    <td> { book.author } </td>
                                    <td> { book.publicationYear } </td>
                                    <td> { book.genre } </td>
                                    <td>
                                        <Link to={`/edit/${book.id}`} className='btn btn-info'><i className="fa-regular fa-pen-to-square"></i></Link>
                                        <button onClick={ () => deleteBook(book.id)} className='btn btn-danger'><i className="fa-solid fa-trash-can"></i></button>
                                    </td>
                                </tr>
                            )) }
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    )
}

export default CompShowBooks
